import { createTool } from "@mastra/core/tools";
import { z } from "zod";

export const analyzeViralReelsTool = createTool({
  id: "analyze-viral-reels",
  description:
    "Analyzes reels to find viral content (posted <3 days ago, 5x higher views than average)",
  inputSchema: z.object({
    accountData: z.array(
      z.object({
        username: z.string(),
        reels: z.array(
          z.object({
            id: z.string(),
            caption: z.string().optional(),
            viewCount: z.number(),
            likeCount: z.number(),
            commentCount: z.number(),
            timestamp: z.string(),
            url: z.string(),
          }),
        ),
      }),
    ),
  }),
  outputSchema: z.object({
    viralReels: z.array(
      z.object({
        username: z.string(),
        reelUrl: z.string(),
        caption: z.string().optional(),
        viewCount: z.number(),
        likeCount: z.number(),
        commentCount: z.number(),
        ageInDays: z.number(),
        growthMultiplier: z.number(),
        averageViews: z.number(),
      }),
    ),
  }),
  execute: async ({ context, mastra }) => {
    const logger = mastra?.getLogger();
    const { accountData } = context;

    logger?.info("üîß [AnalyzeViralReels] Starting execution", {
      accountsCount: accountData.length,
    });

    const viralReels: any[] = [];
    const now = new Date();
    const threeDaysAgo = new Date(now.getTime() - 3 * 24 * 60 * 60 * 1000);

    for (const account of accountData) {
      logger?.info("üìù [AnalyzeViralReels] Processing account", {
        username: account.username,
        reelsCount: account.reels.length,
      });

      if (account.reels.length === 0) {
        logger?.info("‚ö†Ô∏è [AnalyzeViralReels] No reels found for account", {
          username: account.username,
        });
        continue;
      }

      const recentReels = account.reels.filter((reel) => {
        const reelDate = new Date(reel.timestamp);
        return reelDate >= threeDaysAgo;
      });

      const olderReels = account.reels.filter((reel) => {
        const reelDate = new Date(reel.timestamp);
        return reelDate < threeDaysAgo;
      });

      if (recentReels.length === 0) {
        logger?.info("‚ö†Ô∏è [AnalyzeViralReels] No recent reels for account", {
          username: account.username,
        });
        continue;
      }

      const averageViews =
        olderReels.length > 0
          ? olderReels.reduce((sum, reel) => sum + reel.viewCount, 0) /
            olderReels.length
          : account.reels.reduce((sum, reel) => sum + reel.viewCount, 0) /
            account.reels.length;

      logger?.info("üìù [AnalyzeViralReels] Calculated average views", {
        username: account.username,
        averageViews,
        recentReelsCount: recentReels.length,
        olderReelsCount: olderReels.length,
      });

      for (const reel of recentReels) {
        const growthMultiplier = reel.viewCount / averageViews;
        const reelDate = new Date(reel.timestamp);
        const ageInDays = Math.floor(
          (now.getTime() - reelDate.getTime()) / (1000 * 60 * 60 * 24),
        );

        if (growthMultiplier >= 5) {
          logger?.info("üî• [AnalyzeViralReels] Found viral reel!", {
            username: account.username,
            viewCount: reel.viewCount,
            averageViews,
            growthMultiplier: growthMultiplier.toFixed(2),
            ageInDays,
          });

          viralReels.push({
            username: account.username,
            reelUrl: reel.url,
            caption: reel.caption,
            viewCount: reel.viewCount,
            likeCount: reel.likeCount,
            commentCount: reel.commentCount,
            ageInDays,
            growthMultiplier: parseFloat(growthMultiplier.toFixed(2)),
            averageViews: Math.round(averageViews),
          });
        }
      }
    }

    logger?.info("‚úÖ [AnalyzeViralReels] Completed successfully", {
      viralReelsCount: viralReels.length,
    });

    return { viralReels };
  },
});

scripts/build.sh.content:
#!/usr/bin/env bash

set -e

echo "üì¶ Creating production wrapper..."

# Create output directory
mkdir -p .mastra/output

# Create production start script that explicitly sets NODE_ENV
cat > .mastra/output/start-production.sh << 'SCRIPT_EOF'
#!/bin/bash
export NODE_ENV=production
echo "üöÄ Starting Mastra in PRODUCTION mode"
echo "üìù NODE_ENV=$NODE_ENV"
exec node index.mjs
SCRIPT_EOF

chmod +x .mastra/output/start-production.sh

# Create index.mjs with instant health check server
cat > .mastra/output/index.mjs << 'EOF'
import http from 'http';
import { spawn } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const projectRoot = join(__dirname, '../..');

console.log('üöÄ [Production] Starting Instagram Analyzer');

// INSTANT health check server - opens port 5000 immediately
const healthServer = http.createServer((req, res) => {
  res.writeHead(200);
  res.end('OK');
});

healthServer.listen(5000, '0.0.0.0', () => {
  console.log('‚úÖ [Production] Health check server ready on :5000');
  console.log('‚è∞ [Production] Hourly cron: 0 * * * *');

  // Now start Mastra (will take over port 5000)
  healthServer.close(() => {
    const child = spawn('npx', ['mastra', 'dev'], {
      cwd: projectRoot,
      env: { ...process.env, NODE_ENV: 'production' },
      stdio: 'inherit',
    });

    child.on('error', (err) => {
      console.error('‚ùå [Production] Failed:', err);
      process.exit(1);
    });

    child.on('exit', (code) => process.exit(code || 0));

    process.on('SIGTERM', () => child.kill('SIGTERM'));
    process.on('SIGINT', () => child.kill('SIGINT'));
  });
});
EOF

echo "‚úÖ Production wrapper created successfully!"

