import { createTool } from "@mastra/core/tools";
import { z } from "zod";
import { Telegraf } from "telegraf";
import { db } from "../storage";
import { sentViralReels } from "../storage/schema";
import { eq } from "drizzle-orm";

export const sendSingleViralReelTool = createTool({
  id: "send-single-viral-reel",
  description: "Send a single viral reel notification to Telegram",
  inputSchema: z.object({
    username: z.string(),
    reelUrl: z.string(),
    contentType: z.string(), // "Reel", "Video", or "Sidecar" (carousel)
    caption: z.string().optional(),
    viewCount: z.number(),
    likeCount: z.number(),
    commentCount: z.number(),
    ageInDays: z.number(),
    growthMultiplier: z.number(),
    averageViews: z.number(),
    followersCount: z.number(),
  }),
  outputSchema: z.object({
    success: z.boolean(),
    messageId: z.number().optional(),
  }),
  execute: async ({ context, mastra }) => {
    const logger = mastra?.getLogger();
    const {
      username,
      reelUrl,
      contentType,
      caption,
      viewCount,
      likeCount,
      commentCount,
      ageInDays,
      growthMultiplier,
      averageViews,
      followersCount,
    } = context;

    logger?.info("üöÄ [SendSingleViral] Checking if reel was already sent", {
      username,
      reelUrl,
    });

    // Check if reel was already sent
    const existingReel = await db
      .select()
      .from(sentViralReels)
      .where(eq(sentViralReels.reelUrl, reelUrl))
      .limit(1);

    if (existingReel.length > 0) {
      logger?.info("‚è≠Ô∏è [SendSingleViral] Reel already sent, skipping", {
        reelUrl,
        sentAt: existingReel[0].sentAt,
      });
      return {
        success: false,
        messageId: undefined,
      };
    }

    logger?.info("üöÄ [SendSingleViral] Sending viral reel to Telegram", {
      username,
      reelUrl,
    });

    const botToken = process.env.TELEGRAM_BOT_TOKEN;
    // Viral posts go to TELEGRAM_CHAT_ID (References chat) WITHOUT thread
    const chatId = process.env.TELEGRAM_CHAT_ID;

    if (!botToken) {
      throw new Error("TELEGRAM_BOT_TOKEN is not set");
    }

    if (!chatId) {
      throw new Error("TELEGRAM_CHAT_ID is not set");
    }

    const bot = new Telegraf(botToken);

    logger?.info("üìù [SendSingleViral] Telegram settings", {
      chatId,
      thread: "–æ—Å–Ω–æ–≤–Ω–æ–π —á–∞—Ç (–±–µ–∑ –≤–µ—Ç–∫–∏)",
    });

    // Escape HTML characters in caption
    const escapeHtml = (text: string) => {
      return text.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');
    };

    const captionText = caption ? escapeHtml(caption.slice(0, 100) + (caption.length > 100 ? "..." : "")) : "";

    // Determine content emoji and name
    const contentEmoji = contentType === "Sidecar" ? "üñº" : "üé¨";
    const contentName = contentType === "Sidecar" ? "–ö–ê–†–£–°–ï–õ–¨" : "–†–ò–õ–°";

    const message = `
üî• <b>–í–ò–†–£–°–ù–´–ô ${contentName} –ù–ê–ô–î–ï–ù!</b> ${contentEmoji}

üë§ <b>–ê–∫–∫–∞—É–Ω—Ç:</b> @${username}
üë• <b>–ü–æ–¥–ø–∏—Å—á–∏–∫–æ–≤:</b> ${followersCount.toLocaleString()}
üîó <b>–°—Å—ã–ª–∫–∞:</b> ${reelUrl}

üìä <b>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ (–Ω–∞ –º–æ–º–µ–Ω—Ç —Å–∫—Ä–∞–ø–∏–Ω–≥–∞):</b>
üëÅ –ü—Ä–æ—Å–º–æ—Ç—Ä—ã: ${viewCount.toLocaleString()}
‚ù§Ô∏è –õ–∞–π–∫–∏: ${likeCount.toLocaleString()}
üí¨ –ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏: ${commentCount.toLocaleString()}

üìà <b>–ê–Ω–∞–ª–∏–∑ –≤–∏—Ä—É—Å–Ω–æ—Å—Ç–∏:</b>
‚è± –í–æ–∑—Ä–∞—Å—Ç: ${ageInDays} –¥–Ω–µ–π
üöÄ –†–æ—Å—Ç: ${growthMultiplier.toFixed(1)}x –æ—Ç —Å—Ä–µ–¥–Ω–µ–≥–æ
üìä –°—Ä–µ–¥–Ω–∏–π –ø–æ–∫–∞–∑–∞—Ç–µ–ª—å: ${averageViews.toLocaleString()} –ø—Ä–æ—Å–º–æ—Ç—Ä–æ–≤

${captionText ? `üìù <b>–û–ø–∏—Å–∞–Ω–∏–µ:</b> ${captionText}` : ""}

<i>‚ö†Ô∏è –î–∞–Ω–Ω—ã–µ –º–æ–≥—É—Ç –±—ã—Ç—å –Ω–µ –∞–∫—Ç—É–∞–ª—å–Ω—ã, –ø—Ä–æ–≤–µ—Ä—è–π—Ç–µ –Ω–∞ Instagram</i>
`.trim();

    try {
      // Send to main chat without thread ID
      const result = await bot.telegram.sendMessage(chatId, message, {
        parse_mode: "HTML",
      });

      logger?.info("‚úÖ [SendSingleViral] Message sent successfully", {
        messageId: result.message_id,
      });

      // Save sent reel to database
      await db.insert(sentViralReels).values({
        reelUrl,
        username,
      });

      logger?.info("‚úÖ [SendSingleViral] Reel saved to database", {
        reelUrl,
      });

      return {
        success: true,
        messageId: result.message_id,
      };
    } catch (error) {
      logger?.error("‚ùå [SendSingleViral] Failed to send message", {
        error: String(error),
      });

      throw error;
    }
  },
});

src/mastra/tools/getPostOwnerTool.ts.content:
import { createTool } from "@mastra/core/tools";
import { z } from "zod";

export const getPostOwnerTool = createTool({
  id: "get-instagram-post-owner",
  description: "Gets the owner username from an Instagram post URL using Apify",
  inputSchema: z.object({
    postUrl: z.string().describe("Instagram post or reel URL"),
  }),
  outputSchema: z.object({
    username: z.string(),
  }),
  execute: async ({ context, mastra }) => {
    const logger = mastra?.getLogger();
    const { postUrl } = context;

    logger?.info("üîß [GetPostOwner] Starting execution", { postUrl });

    const apifyApiKey = process.env.APIFY_API_KEY;
    if (!apifyApiKey) {
      throw new Error("APIFY_API_KEY is not set");
    }

    logger?.info("üìù [GetPostOwner] Starting Apify actor with directUrls");

    // Use instagram-scraper actor which supports directUrls
    const actorRunResponse = await fetch(
      "https://api.apify.com/v2/acts/apify~instagram-scraper/runs",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${apifyApiKey}`,
        },
        body: JSON.stringify({
          directUrls: [postUrl],
          resultsLimit: 1,
          resultsType: "posts",
        }),
      },
    );

    if (!actorRunResponse.ok) {
      const errorText = await actorRunResponse.text();
      logger?.error("‚ùå [GetPostOwner] Failed to start Apify actor", {
        status: actorRunResponse.status,
        error: errorText,
      });
      throw new Error(
        `Failed to start Apify actor: ${actorRunResponse.statusText}`,
      );
    }

    const runData = await actorRunResponse.json();
    const runId = runData.data.id;

    logger?.info("üìù [GetPostOwner] Waiting for actor to finish", { runId });

    let runStatus = "RUNNING";
    let attempts = 0;
    const maxAttempts = 30;

    while (runStatus === "RUNNING" && attempts < maxAttempts) {
      await new Promise((resolve) => setTimeout(resolve, 3000));

      const statusResponse = await fetch(
        `https://api.apify.com/v2/acts/apify~instagram-scraper/runs/${runId}`,
        {
          headers: {
            Authorization: `Bearer ${apifyApiKey}`,
          },
        },
      );

      const statusData = await statusResponse.json();
      runStatus = statusData.data.status;
      attempts++;

      logger?.info("üìù [GetPostOwner] Actor status", {
        runStatus,
        attempts,
      });
    }

    if (runStatus !== "SUCCEEDED") {
      throw new Error(`Apify actor failed with status: ${runStatus}`);
    }

    logger?.info("üìù [GetPostOwner] Fetching results");

    const resultsResponse = await fetch(
      `https://api.apify.com/v2/actor-runs/${runId}/dataset/items`,
      {
        headers: {
          Authorization: `Bearer ${apifyApiKey}`,
        },
      },
    );

    const results = await resultsResponse.json();

    logger?.info("üìù [GetPostOwner] Raw results", {
      isArray: Array.isArray(results),
      count: results?.length || 0,
      firstItem: results?.[0] || null,
    });

    if (!results || results.length === 0) {
      logger?.error("‚ùå [GetPostOwner] No results returned from Apify", {
        url: postUrl,
        resultsLength: results?.length,
      });
      throw new Error(`No results returned from Apify for URL: ${postUrl}`);
    }

    const postData = results[0];

    // Log the full post data for debugging
    logger?.info("üìù [GetPostOwner] Post data received", {
      url: postUrl,
      hasError: !!postData.error,
      error: postData.error,
      errorDescription: postData.errorDescription,
      availableFields: Object.keys(postData),
    });

    // Check for restricted access error
    if (postData.error === "restricted_page") {
      logger?.error("‚ùå [GetPostOwner] Restricted access to post", {
        url: postUrl,
        errorDescription: postData.errorDescription,
      });
      throw new Error(`Restricted access - post is private or has limited access: ${postUrl}`);
    }

    // Check for other errors
    if (postData.error) {
      logger?.error("‚ùå [GetPostOwner] Apify returned error", {
        url: postUrl,
        error: postData.error,
        errorDescription: postData.errorDescription,
      });
      throw new Error(`Apify error for ${postUrl}: ${postData.error}`);
    }

    // The response should have ownerUsername at the top level or nested
    // Check different possible fields
    let username = 
      postData.ownerUsername || 
      postData.owner?.username ||
      postData.username ||
      postData.ownerFullName;

    // If still no username, try to extract from latestPosts
    if (!username && postData.latestPosts && postData.latestPosts.length > 0) {
      const firstPost = postData.latestPosts[0];
      username = firstPost.ownerUsername || firstPost.owner?.username;
    }

    if (!username) {
      logger?.error("‚ùå [GetPostOwner] Could not find username in results", {
        postData: JSON.stringify(postData).substring(0, 1000),
        keys: Object.keys(postData),
      });
      throw new Error("Could not extract username from post data");
    }

    logger?.info("‚úÖ [GetPostOwner] Successfully extracted username", {
      username,
      postUrl,
    });

    return { username };
  },
});

src/mastra/workflows/instagramAnalysisWo.content:
import { createWorkflow, createStep } from "../inngest";
import { z } from "zod";
import { readGoogleSheetsTool } from "../tools/readGoogleSheetsTool";
import { scrapeInstagramTool } from "../tools/scrapeInstagramTool";
import { sendSingleViralReelTool } from "../tools/sendSingleViralReelTool";
import { getAccountPrioritiesTool } from "../tools/getAccountPrioritiesTool";
import { updateAccountCheckTool } from "../tools/updateAccountCheckTool";
import { RuntimeContext } from "@mastra/core/di";

const runtimeContext = new RuntimeContext();

// Process ALL accounts in a single run - from first to last row
// Each hourly run analyzes the entire Google Sheets list (1000+ accounts)
// Each account analyzed for up to 100 latest reels/carousels
export async function executeInstagramAnalysis(mastra: any) {
  const logger = mastra?.getLogger();
  logger?.info(
    "üöÄ [Workflow] Starting Instagram viral analysis - processing ALL accounts from first to last row",
  );

  // Step 1: Read ALL accounts from Google Sheets (from first to last row)
  logger?.info("üìñ [Step1] Reading ALL Instagram accounts from Google Sheets");

  const { accounts } = await readGoogleSheetsTool.execute({
    context: {},
    mastra,
    runtimeContext,
  });

  logger?.info("‚úÖ [Step1] ALL accounts read successfully", {
    totalAccounts: accounts.length,
    message: "Will analyze all accounts from first to last row",
  });

  // Step 1.5: Prioritize accounts by last check time (never checked first, then oldest)
  logger?.info("üéØ [Step1.5] Prioritizing accounts based on last check time");

  const { prioritizedUsernames, neverChecked, oldestCheckAge } = await getAccountPrioritiesTool.execute({
    context: { allUsernames: accounts },
    mastra,
    runtimeContext,
  });

  logger?.info("‚úÖ [Step1.5] Accounts prioritized", {
    totalAccounts: prioritizedUsernames.length,
    neverChecked,
    oldestCheckAge: oldestCheckAge || "N/A",
    message: "Never-checked accounts first, then oldest checks"
  });

  // Step 2: Process each account and send findings immediately
  let totalViralReelsSent = 0;
  let accountsProcessed = 0;

  for (const accountUrl of prioritizedUsernames) {
    try {
      logger?.info("üìù [Step2] Processing account", { accountUrl });

      // Scrape the account
      const accountData = await scrapeInstagramTool.execute({
        context: { accountUrl },
        mastra,
        runtimeContext,
      });

      logger?.info("‚úÖ [Step2] Account scraped", {
        username: accountData.username,
        reelsCount: accountData.reels.length,
      });

      accountsProcessed++;

      // Track viral reels found for this account
      let viralReelsFoundForAccount = 0;

      // Analyze each reel for virality
      if (accountData.reels.length === 0) {
        logger?.info("‚è≠Ô∏è [Step2] No reels found, skipping", {
          username: accountData.username,
        });

        // Update check history even if no reels found
        await updateAccountCheckTool.execute({
          context: {
            username: accountData.username,
            viralReelsFound: 0,
          },
          mastra,
          runtimeContext,
        });

        continue;
      }

      // Calculate average views for this account
      const totalViews = accountData.reels.reduce(
        (sum, reel) => sum + reel.viewCount,
        0,
      );
      const averageViews = totalViews / accountData.reels.length;

      logger?.info("üìä [Step2] Analyzing reels and carousels for virality", {
        username: accountData.username,
        totalPosts: accountData.reels.length,
        averageViews,
      });

      // Determine adaptive criteria based on account size
      const followersCount = accountData.followersCount;
      let accountSizeCategory: string;
      let viewsMultiplier: number;

      // V10 PROGRESSIVE MULTIPLIER CRITERIA:
      // REELS: Views >= followers * multiplier (smooth progression from X100 to X2)
      // CAROUSELS: Same logic as reels
      // MINIMUM: 100K views for any reel
      // TARGET: 1K followers ‚Üí 100K+ views, 1M followers ‚Üí 2M+ views

      // Calculate progressive multiplier with smooth gradation
      // TARGET: 1K followers ‚Üí 100K views, 1M+ followers ‚Üí 2M views
      // IMPORTANT: Multiplier decreases as followers increase, ensuring monotonic growth
      if (followersCount < 5000) {
        viewsMultiplier = 100; // 1K-5K: 1K√ó100=100K
        accountSizeCategory = "–ú–∏–∫—Ä–æ (1K-5K)";
      } else if (followersCount < 10000) {
        viewsMultiplier = 50; // 5K-10K: 10K√ó50=500K
        accountSizeCategory = "–ú–∏–∫—Ä–æ (5K-10K)";
      } else if (followersCount < 20000) {
        viewsMultiplier = 30; // 10K-20K: 20K√ó30=600K
        accountSizeCategory = "–ú–∏–∫—Ä–æ (10K-20K)";
      } else if (followersCount < 50000) {
        viewsMultiplier = 15; // 20K-50K: 50K√ó15=750K
        accountSizeCategory = "–ú–∞–ª—ã–π (20K-50K)";
      } else if (followersCount < 100000) {
        viewsMultiplier = 10; // 50K-100K: 100K√ó10=1M
        accountSizeCategory = "–ú–∞–ª—ã–π (50K-100K)";
      } else if (followersCount < 200000) {
        viewsMultiplier = 8; // 100K-200K: 200K√ó8=1.6M
        accountSizeCategory = "–°—Ä–µ–¥–Ω–∏–π (100K-200K)";
      } else if (followersCount < 500000) {
        viewsMultiplier = 4; // 200K-500K: 500K√ó4=2M
        accountSizeCategory = "–°—Ä–µ–¥–Ω–∏–π (200K-500K)";
      } else {
        viewsMultiplier = 2; // 500K+: –ú–∞–∫—Å–∏–º–∞–ª—å–Ω—ã–π –º–Ω–æ–∂–∏—Ç–µ–ª—å X2 –¥–ª—è –≤—Å–µ—Ö –±–æ–ª—å—à–∏—Ö –∞–∫–∫–∞—É–Ω—Ç–æ–≤
        accountSizeCategory = followersCount >= 1000000 ? "–ú–µ–≥–∞ (1M+)" : "–ë–æ–ª—å—à–æ–π (500K-1M)";
      }

      // Calculate minimum views
      // For accounts ‚â• 500K: use X2 multiplier but with 2M floor to ensure monotonic growth
      const calculatedMinimum = followersCount * viewsMultiplier;
      let minimumViewsReel: number;

      if (followersCount >= 500000) {
        // For big accounts (‚â•500K): X2 multiplier with 2M minimum
        minimumViewsReel = Math.max(2000000, calculatedMinimum);
      } else {
        // For smaller accounts: 100K minimum
        minimumViewsReel = Math.max(100000, calculatedMinimum);
      }

      // Carousel engagement thresholds (MUCH LOWER than reels)
      // Example: 500K followers ‚Üí 15K engagement is acceptable
      // Using ~3% of followers as baseline
      let carouselMultiplier: number;

      if (followersCount < 10000) {
        carouselMultiplier = 0.5; // 5K ‚Üí 2.5K engagement
      } else if (followersCount < 50000) {
        carouselMultiplier = 0.2; // 30K ‚Üí 6K engagement
      } else if (followersCount < 100000) {
        carouselMultiplier = 0.1; // 80K ‚Üí 8K engagement
      } else if (followersCount < 500000) {
        carouselMultiplier = 0.05; // 300K ‚Üí 15K engagement
      } else {
        carouselMultiplier = 0.03; // 500K+ ‚Üí 3% engagement (500K ‚Üí 15K)
      }

      const carouselCalculated = followersCount * carouselMultiplier;
      const minimumEngagementCarousel = Math.max(5000, carouselCalculated); // Minimum 5K engagement

      logger?.info("üìè [Step2] Virality criteria set (v10 progressive)", {
        username: accountData.username,
        followersCount,
        accountSizeCategory,
        reelsMultiplier: `X${viewsMultiplier}`,
        carouselsMultiplier: `X${carouselMultiplier}`,
        reelsCriteria: `Views >= ${minimumViewsReel.toLocaleString()}`,
        carouselsCriteria: `Engagement >= ${minimumEngagementCarousel.toLocaleString()} (–ª–∞–π–∫–∏+–∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏)`,
      });

      // Check each reel
      for (const reel of accountData.reels) {
        const reelDate = new Date(reel.timestamp);
        const now = new Date();
        const ageInDays = Math.floor(
          (now.getTime() - reelDate.getTime()) / (1000 * 60 * 60 * 24),
        );

        // Skip if older than 60 days
        if (ageInDays > 60) {
          continue;
        }

        // Calculate metrics
        const engagement = reel.likeCount + reel.commentCount;
        const isCarousel = reel.type === "Sidecar";

        // NEW VIRALITY CRITERIA
        let isViral = false;
        let viralityReason = "";

        if (isCarousel) {
          // CAROUSELS: Only engagement-based (no viewCount available in API)
          if (engagement >= minimumEngagementCarousel) {
            isViral = true;
            viralityReason = `Engagement: ${engagement.toLocaleString()} (${reel.likeCount.toLocaleString()} likes + ${reel.commentCount.toLocaleString()} comments) >= ${minimumEngagementCarousel.toLocaleString()} [Carousel]`;
          }
        } else {
          // REELS/VIDEOS: Absolute views threshold
          if (reel.viewCount > 0 && reel.viewCount >= minimumViewsReel) {
            isViral = true;
            viralityReason = `Views: ${reel.viewCount.toLocaleString()} >= ${minimumViewsReel.toLocaleString()} [Reel]`;
          }
        }

        if (isViral) {
          logger?.info("üî• [Step2] VIRAL CONTENT FOUND!", {
            username: accountData.username,
            contentUrl: reel.url,
            contentType: reel.type,
            ageInDays,
            viralityReason,
            viewCount: reel.viewCount,
            engagement,
            followersCount,
            accountSizeCategory,
          });

          // Send immediately to Telegram
          try {
            await sendSingleViralReelTool.execute({
              context: {
                username: accountData.username,
                reelUrl: reel.url,
                contentType: reel.type,
                caption: reel.caption,
                viewCount: reel.viewCount,
                likeCount: reel.likeCount,
                commentCount: reel.commentCount,
                ageInDays,
                growthMultiplier: 0, // Not used in new criteria
                averageViews: 0, // Not used in new criteria
                followersCount: accountData.followersCount,
              },
              mastra,
              runtimeContext,
            });

            totalViralReelsSent++;
            viralReelsFoundForAccount++;

            logger?.info("‚úÖ [Step2] Viral reel sent to Telegram", {
              username: accountData.username,
              reelUrl: reel.url,
            });
          } catch (error) {
            logger?.error("‚ùå [Step2] Failed to send viral reel", {
              username: accountData.username,
              reelUrl: reel.url,
              error: String(error),
            });
          }
        }
      }

      // Update check history after processing this account
      logger?.info("üìù [Step2] Updating check history", {
        username: accountData.username,
        viralReelsFound: viralReelsFoundForAccount,
      });

      await updateAccountCheckTool.execute({
        context: {
          username: accountData.username,
          viralReelsFound: viralReelsFoundForAccount,
        },
        mastra,
        runtimeContext,
      });

    } catch (error) {
      logger?.error("‚ùå [Step2] Error processing account", {
        accountUrl,
        error: String(error),
      });

      // Update check history even on error (to avoid re-checking failed accounts immediately)
      try {
        const username = accountUrl.replace(/^@/, "").split("/").pop() || accountUrl;
        await updateAccountCheckTool.execute({
          context: {
            username,
            viralReelsFound: 0,
          },
          mastra,
          runtimeContext,
        });
      } catch (updateError) {
        logger?.error("‚ùå [Step2] Failed to update check history after error", {
          accountUrl,
          error: String(updateError),
        });
      }
    }
  }

  logger?.info("‚úÖ [Workflow] Analysis complete - ALL accounts processed", {
    totalAccountsProcessed: accountsProcessed,
    totalViralReelsSent,
    totalAccountsInSheet: accounts.length,
  });

  return {
    totalAccountsProcessed: accountsProcessed,
    totalViralReelsSent,
  };
}

// Step wrapper for Inngest workflow
const stepProcessAccountsAndSendFindings = createStep({
  id: "process-accounts-and-send-findings",
  description:
    "Process Instagram accounts, analyze for viral reels and carousels, and send findings immediately",
  inputSchema: z.unknown(),
  outputSchema: z.unknown(),
  execute: async ({ mastra }) => {
    return await executeInstagramAnalysis(mastra);
  },
});

export const instagramAnalysisWorkflow = createWorkflow({
  id: "instagram-viral-analysis",
  description:
    "Analyze Instagram accounts for viral reels and carousels and send instant notifications",
  inputSchema: z.unknown(),
  outputSchema: z.unknown(),
}).then(stepProcessAccountsAndSendFindings).commit();

